import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { login } from '../actions/auth';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [mfaToken, setMfaToken] = useState('');
  const [mfaRequired, setMfaRequired] = useState(false);
  const dispatch = useDispatch();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const result = await dispatch(login(username, password, mfaToken));
    if (result.payload && result.payload.mfa_required) {
      setMfaRequired(true);

    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={username} onChange={e => setUsername(e.target.value)} placeholder="Username" />
      <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password" />
      {mfaRequired && <input type="text" value={mfaToken} onChange={e => setMfaToken(e.target.value)} placeholder="MFA Token" />}
      <button type="submit">Login</button>
    </form>
  );
};

export default Login;
